            vector<int> DP;
            for (int i = 0; i < (int)nums.size(); i++) {
                DP.push_back(1);
            }
            for (int i = 1; i < (int)nums.size(); i++) {
                for (int j = 0; j < i; j++) {
                    if (nums[i] > nums[j]) {
                        DP[i] = max(DP[i], DP[j] + 1);
                    }
                }
            }
            int res = 0;
            for (int i = 0; i < (int)nums.size(); i++) {
                res = max(DP[i], res);

